import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { TaskModel } from 'src/app/models/task-model';
import { DataService } from 'src/app/services/data.service';

@Component({
    selector: 'app-add',
    templateUrl: './add.component.html',
    styleUrls: ['./add.component.css']
})
export class AddComponent implements init{

    public Task = new TaskModel();

    public constructor(private dataService: DataService, private router: Router) { }

    public async send() {
        try {
            this.dataService.addTask(this.Task);
            alert("Task has been successfully added");
            this.router.navigateByUrl("/home");
        }
        catch (err: any) {
            alert(err.message);
        }
    }
}
