    public async addMovie(movie: MovieModel): Promise<void> {
        const observable = this.http.post<MovieModel>(appConfig.moviesUrl, movie);
        await firstValueFrom(observable);
    }

    // Delete movie:
    public async deleteMovie(movieId: string): Promise<void> {
        const observable = this.http.delete(appConfig.moviesUrl + movieId);
        await firstValueFrom(observable);
    }

}
